import React from 'react'
import axios from 'axios';
import {useNavigate} from "react-router-dom"

const Home = () => {

//Uploadボタンを押すと"/replays"に画面遷移させる
    const navigation = useNavigate()
    const handleClick = () => {
        navigation("/replays")
    }

//アイテムの名前を取得
    const [itemData, setItemData] = useState({
        item_name: ''
    })

    const onChange = (e) => {
        setFormData((prevState) => ({
            ...prevState,
            [e.target.name]: e.target.value,
        }))
    }

//ファイルをPOSTする
    const [selectedFile, setSelectedFile] = React.useState(null);
    const UploadFile = (e) => {
        e.preventDefault()
        const formData = new FormData();
        formData.append("selectedFile", selectedFile);
        axios.post("http://localhost:8888/api/upload", {
            name: formData.name
        })
        .then((res) => {
            console.log(res)
            handleClick()
        })
        .catch((error) => {
            console.log(error)
        })
    }

    const getFile = (e) => {
        setSelectedFile(e.target.files[0]);
    }


  return (
    <section className="main">
        <div className="container grid">
            <div className="main-text">
                <h1>Efficient Approach</h1>
                <p>Work out camera provide you a chance to figure out your weak points objectively.  Upload a video of your movement, let's get started! </p>
            </div>

            <div className="main-form card">
                <h2>Quick Start</h2>
                <form onSubmit={UploadFile}>
                    <div className="form-control">
                    <p>Upload your video here</p>
                        <input id="fileupload_1" type="file" name="fileupload_1" accept=".mp4" onChange={getFile} required />
                    </div>
                    <div className="form-control">
                        <input id="fileupload_2" type="file" name="fileupload_2" accept=".mp4" onChange={getFile} required />
                    </div>
                    <div className="form-control">
                        <input type="text" name="Item name" placeholder="Item name" value={item_name} onChange={onChange} required/>
                    </div>
                    <input type="submit" value="Upload" className="btn btn-primary"/>
                </form>
            </div>
        </div>
    </section>
  )
}

export default Home
